(function(e) {
    e.notif = function(t) {
        e("body").notif(t)
    };
    e.fn.notif = function(t) {
		console.log('f');
        var n = {
            html: '<div class="notification {{cls}}">          <div class="left">          	{{#icon}}            <div class="icon">              {{{icon}}}            </div>            {{/icon}}            {{#img}}            <div class="img" style="background-image:url({{img}});"></div>            {{/img}}          </div>          <div class="right">            <h2>{{title}}</h2>            <p>{{content}}</p>          </div>        </div>',
            icon: "&#8505;",
            timeout: 3e3
        };
        if (t.cls == "error") {
            n.icon = "&#10060;"
        }
        if (t.cls == "success") {
            n.icon = "&#10003;"
        }
        var t = e.extend(n, t);
        return this.each(function() {
            var n = e(this);
            var r = e("> .notifications", this);
            var i = e(Mustache.render(t.html, t));
            if (r.length == 0) {
                r = e('<div class="notifications"/>');
                n.append(r)
            }
            r.append(i);
            if (t.timeout) {
                setTimeout(function() {
                    i.trigger("click")
                }, t.timeout)
            }
            i.click(function(e) {
                e.preventDefault();
                i.addClass("out").delay(500).slideUp(300, function() {
                    if (i.siblings().length == 0) {
                        r.remove()
                    }
                    i.remove()
                })
            })
        })
    };
    e("a").click(function(t) {
		console.log('vre');
        t.preventDefault();
        e.notif(e(this).data())
    })
})(jQuery)